clear; echo "Y" | termux-setup-storage &> /dev/null; url="https://raw.githubusercontent.com/KhanhNguyen9872/virtpy_termux/main"; cpu="$(uname -m)"; if [[ $cpu == "aarch64" ]]; then printf ""; else if [[ $cpu == "armv7l" ]] || [[ $cpu == "armv8l" ]]; then url="${url}/bin32"; else printf "\n Không hổ trợ!\n\n"; exit 0; fi; fi; curl -L --max-redirs 15 --progress-bar "${url}/install.sh" --output install.sh || exit 0; chmod 777 ./install.sh; ./install.sh